pragma solidity ^0.5;

import  "./IBEP20.sol";
import "./openzeppelin-contracts-v2.5/ownership/Ownable.sol";
import "./openzeppelin-contracts-v2.5/math/SafeMath.sol";
import "./openzeppelin-contracts-v2.5/token/ERC20/IERC20.sol";
import "./openzeppelin-contracts-v2.5/token/ERC20/ERC20Burnable.sol";
import "./openzeppelin-contracts-v2.5/access/roles/MinterRole.sol";
// comes from //https://github.com/binance-chain/bsc-genesis-contract/blob/master/contracts/bep20_template/BEP20Token.template


contract BEP20 is IBEP20, ERC20 {
  uint8 public decimals;
  string public symbol;
  string public name;
  address private _owner;
  bool public burnable;

  constructor(string memory _name,string memory  _symbol, uint8 _decimals,uint256 _startingBalance,bool _burnable)
  ERC20(_startingBalance)
  public {
    name = _name;
    symbol = _symbol;
    decimals = _decimals;
    _owner=tx.origin;
    burnable=_burnable;
  }
  modifier canBurn() {
    require(burnable, "Erc20: UnBurnable");
    _;
  }

  function burn(uint256 amount)  canBurn public {
    _burn(_msgSender(), amount);
  }

  /**
   * @dev See {ERC20-_burnFrom}.
   */
  function burnFrom(address account, uint256 amount) canBurn public {
    _burnFrom(account, amount);
  }

  /**
   * @dev Returns the bep token owner.
   */
  function getOwner() external view returns (address) {
    return _owner;
  }
  function info() public view returns(string memory,string memory,uint8,uint256,uint256,uint256,bool,bool){
    return (name,symbol,decimals,totalSupply(),balanceOf(msg.sender),0,burnable,false);
  }
  function mintable()   view external returns (bool){
    return false;
  }
}
