pragma solidity ^0.5;

import "./openzeppelin-contracts-v2.5/token/ERC20/IERC20.sol";
import "./openzeppelin-contracts-v2.5/crowdsale/Crowdsale.sol";
import "./openzeppelin-contracts-v2.5/crowdsale/emission/MintedCrowdsale.sol";
import "./openzeppelin-contracts-v2.5/crowdsale/validation/CappedCrowdsale.sol";
import "./openzeppelin-contracts-v2.5/crowdsale/validation/TimedCrowdsale.sol";
import "./openzeppelin-contracts-v2.5/crowdsale/distribution/RefundablePostDeliveryCrowdsale.sol";
import "./BEP20.sol";

// @TODO: Inherit the crowdsale contracts
contract SimpleTokenSale is Crowdsale, CappedCrowdsale, TimedCrowdsale,RefundablePostDeliveryCrowdsale {
  using SafeMath for uint256;
  using SafeMath for uint;
  address  payable operator;
  bool shouldBurn;
  constructor(
    uint rate, // rate in TKNbits
    IERC20 token, // token
    address payable wallet, // company's fundraising wallet that receives ETH sent from users
    uint256 softCap, // goal of the crowdsale
    uint256 hardCap,
    uint256 openingTime, // testing: uint fakenow
    uint256 closingTime, // testing: closingTime = fakenow + 2 minutes,
    bool _shouldBurn
  )
  Crowdsale(rate, wallet, token)
  CappedCrowdsale(hardCap)
  TimedCrowdsale(openingTime, closingTime)
  RefundableCrowdsale(softCap)
    // @TODO: Pass the constructor parameters to the crowdsale contracts.
  public
  {
    operator=msg.sender;
    shouldBurn=_shouldBurn;
    // constructor can stay empty
  }
  function _deliverTokens(address beneficiary, uint256 tokenAmount) internal {
    require(token().transfer(beneficiary, tokenAmount),"TokenTransfer call returned false");
  }
  function _getTokenAmount(uint256 weiAmount)  internal view  returns (uint256)  {
    return weiAmount.mul(rate()).div(10**36);
  }
  function simpleStats() public view returns(uint256,uint256,uint256,uint256,uint256,uint256,uint256,uint256,address,address,bool,bool,bool){
    return (rate(),goal(),cap(),weiRaised(),openingTime(),closingTime(),balanceOf(msg.sender),lockedEthers[msg.sender],address(token()),wallet(),isOpen(),hasClosed(),finalized());
  }
    function tokenInfo() public view returns(string memory,string memory,uint8,uint256,uint256,uint256,bool,bool){
        BEP20 token=(BEP20(address(token())));
        return token.info();
  }
  function _payOperator() internal{
    operator.transfer(weiRaised().div(100));

  }

  function superFinalize()public{
    super._finalization();
  }
  function simpleFinalize()public{
    uint256 _balance=token().balanceOf(address(this));
    if(_balance>0){
      if(shouldBurn){
        BEP20 token=BEP20(address(token()));
        token.burn(_balance);
      }else{
        token().transfer(wallet(),_balance);
      }
    }
    if(capReached()){
      _payOperator();
    }
  }
  function _finalization() internal {
    uint256 _balance=token().balanceOf(address(this));
    if(shouldBurn && _balance >0){
      BEP20 token=BEP20(address(token()));
      token.burn(_balance);
    }else{
      token().transfer(wallet(),_balance);
    }
    if(capReached()){
      _payOperator();
      super._finalization();
    }
  }


}
